sp_widget.client_script:
api.controller = function ($http) {
    /* widget controller */
    var c = this;

    c.selectedOption = '';

    c.filterBySelectedOption = function (item) {

        console.log(item);

        var result = false

        switch (c.selectedOption) {
            case 'all':
                result = true;
                break;
            case 'uatCommitted':
                if(item.uatUpdateset && item.uatUpdateset.state == 'committed'){
                    result = true;
                }
                break;
            case 'uatUncommitted':
                if(item.uatUpdateset && item.uatUpdateset.state != 'committed'){
                    result = true;
                }
                break;
            case 'prodCommitted':
                if(item.prodUpdateset && item.prodUpdateset.state == 'committed'){
                    result = true;
                }
                break;
            case 'prodUncommitted':
                if(item.prodUpdateset && item.prodUpdateset.state != 'committed'){
                    result = true;
                    break;
                }
        }

        return result;
    }

    c.exportTableToExcel = function () {
        var filename = 'updatesets';
        var downloadLink;
        var dataType = 'application/vnd.ms-excel';
        var tableSelect = document.getElementById("update-sets");
        var tableHTML = tableSelect.outerHTML.replace(/ /g, '%20');

        // Specify file name
        filename = filename ? filename + '.xls' : 'excel_data.xlsx';

        // Create download link element
        downloadLink = document.createElement("a");

        document.body.appendChild(downloadLink);

        if (navigator.msSaveOrOpenBlob) {
            var blob = new Blob(['\ufeff', tableHTML], {
                type: dataType
            });
            navigator.msSaveOrOpenBlob(blob, filename);
        } else {
            // Create a link to the file
            downloadLink.href = 'data:' + dataType + ', ' + tableHTML;

            // Setting the file name
            downloadLink.download = filename;

            //triggering the function
            downloadLink.click();
        }

    }


};

